mapper.select:
select subject.*, ma_name from subject join major on su_ma_num = ma_num
where su_name like concat('%', #{cri.search}, '%')
order by su_num ASC limit #{cri.pageStart}, #{cri.perPageNum}

mapper.update:
update
	subject
set
	su_name = #{su.su_name}
	, su_time = #{su.su_time}
	, su_point = #{su.su_point}
	, su_status = #{su.su_status}
where
	su_num = #{su.su_num}
